//Plugin name        : inewsticker - jQuery news ticker
//Version            : 0.1.0
//Url	 			 : http://www.ijquery.ir/effect/inewsticker/
(function (e) {
  e.fn.ticker = function (t) {
    var n = {
      speed: 200,
      effect: "fade",
      dir: "ltr",
      font_size: null,
      color: null,
      font_family: null,
      delay_after: 3e3,
    };
    e.extend(n, t);
    var r = e(this);
    var i = r.children();
    i.not(":first").hide();
    r.css("direction", t.dir);
    r.css("font-size", t.font_size);
    r.css("color", t.color);
    r.css("font-family", t.font_family);
    setInterval(function () {
      var e = r.children();
      e.not(":first").hide();
      var n = e.eq(0);
      var i = e.eq(1);
      if (t.effect == "fade") {
        n.fadeOut(function () {
          i.fadeIn();
          n.remove().appendTo(r);
        });
      }
    }, t.speed);
  };
})(jQuery);
